import React, { useState } from "react";
import styles from "../../../../styles/components/common/lists/MpWorkItem.module.scss";
import MpPopup from "../../MpPopup";
import MpCheckImageWebp from "../../../../hooks/MpCheckImageWebp";

function MpWorkItem( {workName, workClass, workDate, url, target, contents, index} ) {
    const [ showPopup, setShowPopup ] = useState(false);
    const isWebPSupport = MpCheckImageWebp();

    const handlePopup = () => {
        setShowPopup(true);
    };

    const handleClickClose = () => {
        setShowPopup(!showPopup);
    }

    return (
        <>
            <li className={styles['main_gallery_item']} key={index}>
                <button className={`${styles['main_gallery_link']} ${isWebPSupport ? styles['support_webp'] : ''} ${styles[workClass]}`} aria-haspopup="dialog"
                        aria-controls={workClass} onClick={handlePopup}>
                    <span className={styles['main_gallery_text_wrap']}>
                        <strong className={styles['main_gallery_text']}>{workName}</strong>
                        <span className={styles['main_gallery_date']}>{workDate}</span>
                    </span>
                </button>

                {showPopup &&
                    <MpPopup
                        workName={workName}
                        workClass={workClass}
                        workDate={workDate}
                        url={url}
                        target={target}
                        contents={contents}
                        onClose={() => handleClickClose(false)}
                    />
                }
            </li>
        </>
    );
}

export default MpWorkItem;
